import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BugItem from '../components/BugItem';

const BugList = () => {
  const [bugs, setBugs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBugs = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/bugs');
        setBugs(response.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchBugs();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/bugs/${id}`);
      setBugs(bugs.filter(bug => bug._id !== id));
    } catch (err) {
      console.error('Failed to delete bug:', err);
    }
  };

  const handleStatusChange = async (id, status) => {
    try {
      const response = await axios.put(`/api/bugs/${id}`, { status });
      setBugs(bugs.map(bug => bug._id === id ? response.data : bug));
    } catch (err) {
      console.error('Failed to update bug status:', err);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div data-testid="bug-list">
      <h1>Bug Tracker</h1>
      {bugs.length === 0 ? (
        <p>No bugs found. Create one!</p>
      ) : (
        bugs.map(bug => (
          <BugItem 
            key={bug._id} 
            bug={bug} 
            onDelete={handleDelete}
            onStatusChange={handleStatusChange}
          />
        ))
      )}
    </div>
  );
};

export default BugList;
