import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const BugForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    status: 'open'
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (id) {
      const fetchBug = async () => {
        try {
          const response = await axios.get(`/api/bugs/${id}`);
          setFormData(response.data);
        } catch (err) {
          console.error('Failed to fetch bug:', err);
        }
      };
      fetchBug();
    }
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Simple validation
    const newErrors = {};
    if (!formData.title) newErrors.title = 'Title is required';
    if (!formData.description) newErrors.description = 'Description is required';
    
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      if (id) {
        await axios.put(`/api/bugs/${id}`, formData);
      } else {
        await axios.post('/api/bugs', formData);
      }
      navigate('/');
    } catch (err) {
      console.error('Failed to save bug:', err);
    }
  };

  return (
    <div>
      <h2>{id ? 'Edit Bug' : 'Report New Bug'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            data-testid="bug-title"
          />
          {errors.title && <span>{errors.title}</span>}
        </div>
        
        <div>
          <label>Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            data-testid="bug-description"
          />
          {errors.description && <span>{errors.description}</span>}
        </div>
        
        <div>
          <label>Status</label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            data-testid="bug-status"
          >
            <option value="open">Open</option>
            <option value="in-progress">In Progress</option>
            <option value="resolved">Resolved</option>
          </select>
        </div>
        
        <button type="submit" data-testid="submit-bug">
          {id ? 'Update Bug' : 'Submit Bug'}
        </button>
      </form>
    </div>
  );
};

export default BugForm;
